import { Component } from '@angular/core';

@Component({
  selector: 'app-opinion-card',
  templateUrl: './opinion-card.component.html',
  styleUrls: ['./opinion-card.component.scss'],
})
export class OpinionCardComponent {
  opinion = {
    avatar: '../../../../../assets/images/avatar/patient-1.jpg',
    name: 'Jhon Doe',
    text: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, voluptatum. Lorem ipsum dolor sit amet consectetur adipisicing elit. Quisquam, voluptatum.',
    rol: 'Paciente',
  };
}
<article>
  <div class="opinion-card">
    <div class="opinion-card-header">
      <div class="opinion-card-header-avatar">
        <img src="{{ opinion.avatar }}" alt="{{ opinion.name }}" />
      </div>
      <div class="opinion-card-header-info">
        <h3 class="opinion-card-header-info-name">{{ opinion.name }}</h3>
        <p class="opinion-card-header-info-position">
          {{ opinion.rol }}
        </p>
      </div>
    </div>
    <div class="opinion-card-body">
      <p class="opinion-card-body-text">{{ opinion.text }}</p>
    </div>
  </div>
</article>
