import { Component } from '@angular/core';

@Component({
  selector: 'app-specialty-card',
  templateUrl: './specialty-card.component.html',
  styleUrls: ['./specialty-card.component.scss'],
})
export class SpecialtyCardComponent {
  specialties = {
    image: '../../../../../assets/images/specialties/odontologia-1.svg',
    name: 'Odontología',
    description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    id: 1,
  };
}
<article>
  <div class="specialty-card">
    <div class="specialty-card-image">
      <img [src]="specialties.image" [alt]="specialties.name" />
    </div>
    <div class="specialty-card-content">
      <h3 class="specialty-card-title">{{ specialties.name }}</h3>
      <p class="specialty-card-description">{{ specialties.description }}</p>
      <a
        class="specialty-card-link"
        [routerLink]="['/specialties', specialties.id]"
        >Ver más <span class="arrow-right">→</span></a
      >
    </div>
  </div>
</article>
