import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';
import { NotifyService } from 'src/app/services/notify.service';
import { UserLogin } from 'src/app/shared/models/auth.model';

@Component({
  selector: 'app-layout-login',
  templateUrl: './layout-login.component.html',
  styleUrls: ['./layout-login.component.scss'],
})
export class LayoutLoginComponent {
  loginForm = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', [Validators.required]),
  });

  constructor(
    private authSvc: AuthService,
    private notifySvc: NotifyService,
    private router: Router
  ) {}

  get email() {
    return this.loginForm.get('email');
  }

  get password() {
    return this.loginForm.get('password');
  }

  login() {
    const email = this.email?.value;
    const password = this.password?.value;

    if (!email || !password) {
      this.notifySvc.showError('Verifica tus datos', 'Error al iniciar sesión');
      return;
    }

    const user: UserLogin = {
      email,
      password,
    };

    this.authSvc.login(user).subscribe({
      next: () => {
        this.notifySvc.showSuccess('Bienvenido', 'Inicio de sesión exitoso');
      },
      complete: () => {
        this.router.navigate(['/doctime']);
      },
    });
  }
}
<div class="login-container">
  <div class="login-form">
    <div class="login-form-content">
      <div class="logo">
        <img src="../../../assets/images/logo.svg" alt="logo" />
        <h1>DocTime</h1>
      </div>
      <form [formGroup]="loginForm">
        <div class="form-group">
          <label for="email">Correo Electronico</label>
          <input type="email" id="email" name="email" formControlName="email" />
        </div>
        <div class="form-group">
          <label for="password">Contraseña</label>
          <input
            type="password"
            id="password"
            name="password"
            formControlName="password"
          />
        </div>
        <button class="btn-form" (click)="login()">Iniciar Sesión</button>
        <div class="links">
          <a href="#">¿Olvidaste tu contraseña?</a>
          <a [routerLink]="['/register']">Registrarse</a>
        </div>
      </form>
    </div>
    <div class="login-form-img">
      <img
        title="Bienvenid@ a DocTime"
        src="../../../assets/images/panel-login.svg"
        alt="panel login"
      />
    </div>
  </div>
</div>
